import React, { useState } from 'react';
import './App.css';

function App() {
  const [videoFile, setVideoFile] = useState(null);
  const [polygons, setPolygons] = useState('');
  const [processedVideo, setProcessedVideo] = useState(null);
  const [loading, setLoading] = useState(false);

  const handleVideoUpload = (event) => {
    setVideoFile(event.target.files[0]);
    setProcessedVideo(null);
  };

  const handlePolygonsChange = (event) => {
    setPolygons(event.target.value);
  };

  const handleProcessVideo = async () => {
    if (!videoFile || !polygons) {
      alert('Please upload a video and define polygons.');
      return;
    }

    const formData = new FormData();
    formData.append('file', videoFile);
    formData.append('polygons', polygons);

    setLoading(true);

    const response = await fetch('http://localhost:8000/upload/', {
      method: 'POST',
      body: formData,
    });

    if (response.ok) {
      const blob = await response.blob();
      setProcessedVideo(URL.createObjectURL(blob));
    } else {
      alert('Failed to process video.');
    }

    setLoading(false);
  };

  return (
    <div className="App">
      <h1>YOLO Person Counter</h1>
      <input type="file" accept="video/*" onChange={handleVideoUpload} />
      <video src={videoFile ? URL.createObjectURL(videoFile) : ''} controls />
      <h2>Define Polygons</h2>
      <textarea value={polygons} onChange={handlePolygonsChange} rows="5" cols="50"></textarea>
      <button onClick={handleProcessVideo}>Apply Analytic</button>
      {loading && <div>Processing...</div>}
      {processedVideo && (
        <div>
          <h2>Processed Video</h2>
          <video src={processedVideo} controls />
        </div>
      )}
    </div>
  );
}

export default App;
