import React, { useState, useRef, useEffect } from 'react';
import './App.css';
import { Stage, Layer, Line } from 'react-konva';

function App() {
  const [videoFile, setVideoFile] = useState(null);
  const [videoURL, setVideoURL] = useState(null);
  const [processedVideo, setProcessedVideo] = useState(null);
  const [loading, setLoading] = useState(false);
  const [polygons, setPolygons] = useState([]);
  const [drawing, setDrawing] = useState(false);
  const [isDrawingStage, setIsDrawingStage] = useState(false);
  const videoRef = useRef(null);
  const stageRef = useRef(null);

  useEffect(() => {
    if (videoFile) {
      const url = URL.createObjectURL(videoFile);
      setVideoURL(url);
      return () => URL.revokeObjectURL(url);
    }
  }, [videoFile]);

  const handleVideoUpload = (event) => {
    setVideoFile(event.target.files[0]);
    setProcessedVideo(null);
  };

  const handleMouseDown = (event) => {
    if (isDrawingStage && !drawing) {
      const { x, y } = event.target.getStage().getPointerPosition();
      setPolygons([...polygons, [{ x, y }]]);
      setDrawing(true);
    }
  };

  const handleMouseMove = (event) => {
    if (isDrawingStage && drawing) {
      const { x, y } = event.target.getStage().getPointerPosition();
      const lastPolygon = polygons[polygons.length - 1];
      lastPolygon.push({ x, y });
      setPolygons(polygons.slice(0, -1).concat([lastPolygon]));
    }
  };

  const handleMouseUp = () => {
    if (isDrawingStage) {
      setDrawing(false);
    }
  };

  const handleStartDrawing = () => {
    setIsDrawingStage(true);
  };

  const handleProcessVideo = async () => {
    if (!videoFile || polygons.length === 0) {
      alert('Please upload a video and define a polygon.');
      return;
    }

    const polygonData = polygons.map((polygon) =>
      polygon.map((point) => [point.x, point.y])
    );

    const formData = new FormData();
    formData.append('video', videoFile);
    formData.append('polygon', JSON.stringify(polygonData));

    setLoading(true);

    try {
      const response = await fetch('http://localhost:8000/analyze', {
        method: 'POST',
        body: formData,
      });

      if (response.ok) {
        const data = await response.json();
        const videoURL = `http://localhost:8000/videos/${data.video_filename}`;
        console.log('Processed video URL:', videoURL); // Log do URL do vídeo processado
        setProcessedVideo(videoURL);
      } else {
        alert('Failed to process video.');
      }
    } catch (error) {
      console.error('Error:', error);
      alert('Error processing video.');
    }

    setLoading(false);
  };

  // Logs para evitar avisos de variáveis não utilizadas
  console.log('Processed Video:', processedVideo);
  console.log('Loading:', loading);

  return (
    <div className="App">
      <h1>YOLO Person Counter</h1>
      <input type="file" accept="video/*" onChange={handleVideoUpload} />
      <div className="video-container" style={{ position: 'relative' }}>
        <video ref={videoRef} src={videoURL} controls />
        {videoURL && isDrawingStage && (
          <Stage
            ref={stageRef}
            width={videoRef.current ? videoRef.current.clientWidth : 800}
            height={videoRef.current ? videoRef.current.clientHeight : 600}
            onMouseDown={handleMouseDown}
            onMouseMove={handleMouseMove}
            onMouseUp={handleMouseUp}
            style={{ position: 'absolute', top: 0, left: 0 }}
          >
            <Layer>
              {polygons.map((polygon, index) => (
                <Line
                  key={index}
                  points={polygon.flatMap((point) => [point.x, point.y])}
                  stroke="red"
                  strokeWidth={2}
                  closed
                />
              ))}
            </Layer>
          </Stage>
        )}
      </div>
      {!isDrawingStage && videoURL && (
        <button onClick={handleStartDrawing}>Start Drawing Polygons</button>
      )}
      {isDrawingStage && (
        <button onClick={handleProcessVideo}>Apply Analytic</button>
      )}
    </div>
  );
}

export default App;
